<?php defined('SYSPATH') or die('No direct script access.'); ?><script type="text/javascript">$(document).ready(function(){	$('input#logemail').focus(function(){		var email = $(this); var password = $('input#logpassword');		if (email.val() == '<?php echo i18n::get('email'); ?>') { email.val('').removeClass('disactive'); } 		if (password.val()=='<?php echo i18n::get('password'); ?>'){ password.val('').removeClass('disactive'); } 	});	$('input#logemail').blur(function(){		var email = $(this); var password = $('input#logpassword');		if (email.val() == '') { email.val('<?php echo i18n::get('email'); ?>'); email.addClass('disactive');} 		if (password.val()==''){ password.val('<?php echo i18n::get('password'); ?>'); password.addClass('disactive'); }     	});	$('input#logpassword').focus(function(){		var email = $('input#logemail'); var password = $(this);		if (email.val() == '<?php echo i18n::get('email'); ?>') { email.val('').removeClass('disactive'); } 		if (password.val()=='<?php echo i18n::get('password'); ?>'){ password.val('').removeClass('disactive'); } 	});	$('input#logpassword').blur(function(){		var password = $(this); var email = $('input#logemail');		if (email.val() == '') { email.val('<?php echo i18n::get('email'); ?>'); email.addClass('disactive');} 		if (password.val()==''){ password.val('<?php echo i18n::get('password'); ?>'); password.addClass('disactive'); }  	});})</script><div class="container-fluid">	<div class="fixed_login">		<div class="logo">			<a href="<?php echo URL::site();?>" title="Logo" target="_blank">				<img alt="Logo" style="margin:0 auto;" src="<?php echo IMG . THM;?>content/logo_big.png"/>			</a>		</div>		<div id="authentication"> 			<?php echo Form::open(ADMIN . 'authentication/validate', array('id'=>"login_admin",'class'=>"auto_valid general"));?>					<label class="pull-left left loginLabel">Email</label>				<div class="pull-left form_row">					<input type="text" name="email" id="logemail" class="form-control required <?php echo empty($fields['email']) ? 'disactive' : ''; ?>" value="<?php echo empty($fields['email']) ? i18n::get('email') : $fields['email']; ?>" style="width: 132px;"/>				</div>				<div class="clear ls5"></div>				<label class="pull-left left loginLabel">Password</label>				<div class="pull-left form_row">					<input type="password" name="password" id="logpassword" class="form-control required <?php echo empty($fields['email']) ? 'disactive':''; ?>" value="<?php echo empty($fields['password']) ? i18n::get('password') : $fields['password'];?>" style="width: 132px;" />				</div>				<div class="clear ls10"></div>				<div class="right">					<input type="submit" class="btn btn-large btn-block btn-primary" value="Login"/>				</div>				<div class="clear ls5"></div>				<?php 					echo !empty($errors['email']) 					? '<div class="alert alert-warning auth_error"><strong>'.$errors['email'].'</strong></div>' 					: ''; 				?>				<?php 					echo !empty($errors['password']) 					? '<div class="alert alert-warning auth_error"><strong>'.$errors['password'].'</strong></div>' 					: ''; ?>				<?php 					echo !empty($errors['error_login']) 					? '<div class="alert alert-warning auth_error"><strong>'.$errors['error_login'].'</strong></div>' 					: ''; 				?>				<div class="clear ls15"></div>			<?php echo Form::close();?>		</div>	</div></div>